package com.demo2;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=10;
        while(i<=100) {    // do while -This will execute code block one before changing if the condition is true
        	             //.Then it repeat the loop as known as condition is true
        	
        	System.out.println(i);
        	i+=10;
        	
        }
	}

}
